import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  // State to control mode: true for input (form), false for output (data display)
  const [isInputMode, setIsInputMode] = useState(true);

  // State for form data
  const [message, setMessage] = useState("");
  // State for storing fetched messages
  const [messages, setMessages] = useState([]);

  // Function to toggle between input and output
  const toggleMode = () => {
    setIsInputMode(!isInputMode);
  };

  // Function to handle form submission
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3001/api/messages', { message });
      setMessage(""); // Clear input field after submission
    } catch (error) {
      console.error("Error submitting message:", error);
    }
  };

  // Fetch messages when switching to output mode
  useEffect(() => {
    if (!isInputMode) {
      const fetchMessages = async () => {
        try {
          const response = await axios.get('http://localhost:3001/api/messages');
          setMessages(response.data);
        } catch (error) {
          console.error("Error fetching messages:", error);
        }
      };
      fetchMessages();
    }
  }, [isInputMode]);

  return (
    <div className="App">
    <h1>{isInputMode ? "Input Mode" : "Output Mode"}</h1>
    <button onClick={toggleMode}>
    Switch to {isInputMode ? "Output" : "Input"} Mode
    </button>

    {isInputMode ? (
      // Input Form
      <form onSubmit={handleFormSubmit}>
      <input
      type="text"
      value={message}
      onChange={(e) => setMessage(e.target.value)}
      placeholder="Enter your message"
      />
      <button type="submit">Submit</button>
      </form>
    ) : (
      // Output Data Display
      <div>
      <h2>Messages:</h2>
      <ul>
      {messages.map((msg, index) => (
        <li key={index}>{msg.message}</li>
      ))}
      </ul>
      </div>
    )}
    </div>
  );
}

export default App;
